package Day4;

public class SmallestNumberOf2Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a =10,b=22,c =3;
		
		if(a<b && a<c)
		{
			System.out.println("Smallest number is a \n"+a);
		}
		else if(b<a && b<a)
		{
			System.out.println("Smallest number is b \n"+b);
		}
		else
		{
			System.out.println("Smallest number is c \n"+c);
		}
		
		
		
		

	}

}
